(window.webpackJsonp=window.webpackJsonp||[]).push([[108],{554:function(t,a,r){"use strict";r.r(a);var s=r(10),v=Object(s.a)({},(function(){var t=this,a=t.$createElement,r=t._self._c||a;return r("ContentSlotsDistributor",{attrs:{"slot-key":t.$parent.slotKey}},[r("h2",{attrs:{id:"中国金融体系简略图"}},[r("a",{staticClass:"header-anchor",attrs:{href:"#中国金融体系简略图"}},[t._v("#")]),t._v(" 中国金融体系简略图")]),t._v(" "),r("p",[r("img",{attrs:{src:"/img/Other/Finance/006tNbRwly1fvnrbuaszrj30te0oeamn.jpg",alt:"中国的金融体系简略图"}})]),t._v(" "),r("h2",{attrs:{id:"中国金融体系里有6个主要实体"}},[r("a",{staticClass:"header-anchor",attrs:{href:"#中国金融体系里有6个主要实体"}},[t._v("#")]),t._v(" 中国金融体系里有6个主要实体：")]),t._v(" "),r("p",[t._v("人民银行、政府、商业银行、证券公司等金融投资机构、企业、个人。理解金融体系只不过需要了解这6个实体的功能，及相互之间的关系。")]),t._v(" "),r("h3",{attrs:{id:"人民银行"}},[r("a",{staticClass:"header-anchor",attrs:{href:"#人民银行"}},[t._v("#")]),t._v(" 人民银行")]),t._v(" "),r("p",[t._v("百科上说，人民银行是国务院领导下的国家银行，又称“央行”。国家银行是啥概念，就是说投资人是国家。和其他银行不一样的地方在于国家银行不已盈利为目地，如果以盈利为目的，国家银行开动印钞机，国家银行富了，大家口袋里的钱贬值了也就穷了。这当然不利于国家长期稳定发展。俗话说“藏富于民”，民富才是国家昌盛的象征。")]),t._v(" "),r("p",[t._v("人民银行是“钱”的源头，可以被称作“lord of the money”。我们口袋里的钱，银行存款上的钱，房贷借的钱，公司付给我们的工资，源头都来自人民银行。通俗的说，人民银行是“印钞机”和“钞票粉丝机”。人民银行在市场需要更多资金时，可以启动“印钞”功能，向市场注入货币。在市场上货币泛滥时，可以启动“钞票粉碎机”的功能，将货币回收。")]),t._v(" "),r("p",[t._v("人民银行往市场上投入钱的方法主要是对商业银行“再贴现”及“再贷款”，以及在公开市场上购买政府发行的国债。")]),t._v(" "),r("h3",{attrs:{id:"政府"}},[r("a",{staticClass:"header-anchor",attrs:{href:"#政府"}},[t._v("#")]),t._v(" 政府")]),t._v(" "),r("p",[t._v("各级政府和公司一样，需要资金来维持日常运营，也需要投资建公路铁路等基础设施。政府的资金来源主要是税务、国企收入、政务收费及债务。税大家都很熟悉了，企业要缴纳企业所得税等，个人要缴个税。国企占据各垄断行业，收入不错。政务是行政单位的各种收费如违章停车的罚款。还有一个重要来源是国债和地方债。")]),t._v(" "),r("h3",{attrs:{id:"商业银行"}},[r("a",{staticClass:"header-anchor",attrs:{href:"#商业银行"}},[t._v("#")]),t._v(" 商业银行")]),t._v(" "),r("p",[t._v("大家的钱都存在商业银行里吧（习惯把钞票塞棉鞋里的除外），没人不了解商业银行。商业银行本质上也是公司，只不过运营的是钱而不是商品。大家把自己积攒的钱存在银行里，银行把汇总的存款一批批贷款给各个企业，收利息，同时给存款人一点更少的利息，商业银行的主营业务其实就这么简单。前几年房地产热时，银行一头给开发商提供贷款，一头给买房者提供贷款，生意好的不得了。")]),t._v(" "),r("h3",{attrs:{id:"证券、基金公司等金融投资机构"}},[r("a",{staticClass:"header-anchor",attrs:{href:"#证券、基金公司等金融投资机构"}},[t._v("#")]),t._v(" 证券、基金公司等金融投资机构")]),t._v(" "),r("p",[t._v("银行利息这么低，甚至赶不上通货膨胀率，越来越多的人把钱投到预期回报率更高的产品上，例如股票、基金等。证券公司（券商）、基金公司在此类投资活动中发挥巨大作用，而且业务越来越多元化。以证券公司为例，既帮客户下单买股票（经纪），又帮公司上市（投行）、还自己炒炒股票（自营）、再帮富人理理财（资产管理），忙的不亦乐乎。")]),t._v(" "),r("h3",{attrs:{id:"企业"}},[r("a",{staticClass:"header-anchor",attrs:{href:"#企业"}},[t._v("#")]),t._v(" 企业")]),t._v(" "),r("p",[t._v("前面说了半天，现在才真正说到经济的实体。我们说振兴民族经济，说促进市场繁荣，不是说让银行富起来、不是说让证券公司富起来，而是让企业富起来让个人富起来。企业，特别是中小企业，是发达国家经济繁荣的象征和基础。中国的中小企业贡献了65%的GDP，解决了75%城市就业问题。")]),t._v(" "),r("p",[t._v("企业，特别是中小企业的发展离不开资金。获取资金的通常方式，是通过创始人的初始资本，和经营企业时产生的盈余利润获取。")]),t._v(" "),r("p",[t._v("运营中需要人手，因此需要招聘员工，这样就解决了城市人口就业问题，当然也需要支付员工薪酬。企业的税务压力很重，增值税、企业所得税、城建税、教育费附加、车船税、各类社会保险费用。这样，企业结余的资金较为有限，想谋求大规模发展，往往需要通过融资的手段。融资时资金可来源于银行贷款、风险投资机构、股票市场等，方式可分为股权和债权。吸纳股权投资时，公司现有股东需要付出公司的股份，让投资者成为新股东。这样做的好处是不用还钱，没有还债压力。吸纳债权投资说白了就是借钱，借了是要连本带利还的，好处是公司的股份不会外流。")]),t._v(" "),r("h3",{attrs:{id:"个人"}},[r("a",{staticClass:"header-anchor",attrs:{href:"#个人"}},[t._v("#")]),t._v(" 个人")]),t._v(" "),r("p",[t._v("也就是人民，是你和我。我们通过辛勤劳动，赚点钱不容易。一部分钱用于消费，买房买车买菜都是消费。一部分存在银行里，回报率很低但起码没有风险。一部分投资到股市、基金等投资产品里，风险高点，预期回报也高点。我们在消费、投资、存款的时候，钱又回到了金融体系中，在金融体系中循环流转。")])])}),[],!1,null,null,null);a.default=v.exports}}]);